<template>
  <div class="sorts vux-1px-b">
    <div class="sorts-item" v-for="(item,index) in sort" @click="showrouter(index)">
      <img width="56" height="46" :src="item.src">
      <p class="name">{{item.title}}</p>
    </div>
  </div>
</template>

<script type="text/ecmascript-6">
  export  default {
    data() {
      return {
        sort:[{
            title: "景区",
            src: "data:image/png;base64,"
          },
          {
            title: "农家乐",
            src: "data:image/png;base64,"
          },
          {
            title: "团队定制",
            src: "data:image/png;base64,"
          }]
      }
    },
    methods:{
      showrouter(index){
        if (index === 2 ){
          this.$router.push('teamcustom')
        }else if(index === 1){
          this.$router.push({path:'searchlist',query:{type:'farm'}})
        }else if(index === 0){
          this.$router.push({path:'searchlist',query:{type:'sight'}})
        }
      }
    }
  }
</script>

<style lang="less">
  @import '../../../node_modules/vux/src/styles/1px.less';
  .sorts{
    display: flex;
    padding:12px 0;
    font-size:0px;
    .sorts-item{
      flex:1;
      text-align: center;
      .name{
        font-size: 11px;
        font-family: 微软雅黑;
      }
    }
  }


</style>
